    public static List<MovieEntity> getMovies() {
        List<MovieEntity> resultsItemNowPlayingList = new ArrayList<>();
        try {
            JSONArray array = new JSONArray(nowPlayData);
            for (int i = 0; i < array.length(); i++) {
                JSONObject object = array.getJSONObject(i);

                int id = object.getInt("id");
                String title = object.getString("title");
                String releaseDate = object.getString("release_date");
                String description = object.getString("overview");
                int voteAverage = object.getInt("vote_average");
//                String originalLanguage = object.getString("original_language");
                String poster = object.getString("poster_path");

                resultsItemNowPlayingList.add(new MovieEntity(id, title, description, releaseDate, poster));
            }
        } catch (JSONException e) {
            e.printStackTrace();
        }
        return resultsItemNowPlayingList;
    }

